  function string_min($value, $name, $minimum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is shorter than minimum length of strings at") . " '" . $minimum . "' " . $this->translation->translate("character(s)");
    if (!$this->is_empty($value)) {
      if (function_exists("mb_strlen")) {
        if (mb_strlen($value) < $minimum) {
          $result = false;
          throw new Exception($message, 400);
        }
      } else {
        if (strlen($value) < $minimum) {
          $result = false;
          throw new Exception($message, 400);
        }
      }
    }
    return $result;
  }

  function string_max($value, $name, $maximum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is shorter than maximum length of strings at") . " '" . $maximum . "' " . $this->translation->translate("character(s)");
    if (!$this->is_empty($value)) {
      if (function_exists("mb_strlen")) {
        if (mb_strlen($value) < $maximum) {
          $result = false;
          throw new Exception($message, 400);
        }
      } else {
        if (strlen($value) < $maximum) {
          $result = false;
          throw new Exception($message, 400);
        }
      }
    }
    return $result;
  }

  function number_min($value, $name, $minimum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is less than minimum number at") . " '" . $minimum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) > date_create($minimum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function number_max($value, $name, $maximum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is more than maximum number at") . " '" . $maximum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) < date_create($maximum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function date_min($value, $name, $minimum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is before minimum date at") . " '" . $minimum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) > date_create($minimum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function date_max($value, $name, $maximum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is after maximum date at") . " '" . $maximum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) < date_create($maximum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function datetime_min($value, $name, $minimum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is before minimum date and time at") . " '" . $minimum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) > date_create($minimum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function datetime_max($value, $name, $maximum) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("is after maximum date and time at") . " '" . $maximum . "'";
    if (!$this->is_empty($value)) {
      if (date_create($value) < date_create($maximum)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function no_spaces($value, $name) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("does not allow white spaces");
    if (!$this->is_empty($value)) {
      if (function_exists("str_contains")) {
        if (str_contains($value, " ")) {
          $result = false;
          throw new Exception($message, 400);
        }
      } else {
        if (!$this->is_empty(strpos($value, " "))) {
          $result = false;
          throw new Exception($message, 400);
        }
      }
    }
    return $result;
  }

  function no_special_characters($value, $name, $level = "basic") {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("does not allow special characters and white spaces excluding ., -, _");
    $pattern = "/[^a-zA-Z\d\.\-]/";
    if ($level == "strict") {
      $pattern = "/[^a-zA-Z\d]/";
      $message = $this->translation->translate($name) . " " . $this->translation->translate("does not allow special characters and white spaces");
    }
    if (!$this->is_empty($value)) {
      if (!preg_match($pattern, $value)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function uppercase_only($value, $name) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("allows only uppercase character(s)");
    $pattern = "/^[A-Z0-9]+$/";
    if (!$this->is_empty($value)) {
      if (!preg_match($pattern, $value)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function lowercase_only($value, $name) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("allows only lowercase character(s)");
    $pattern = "/^[a-z0-9]+$/";
    if (!$this->is_empty($value)) {
      if (!preg_match($pattern, $value)) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function contains_prefix($value, $name, $prefix) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("must contain prefix") . " '" . $prefix . "'";
    if (!$this->is_empty($value)) {
      if (strpos($value, $prefix) === 0) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function contains_suffix($value, $name, $suffix) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("must contain suffix") . " '" . $suffix . "'";
    if (!$this->is_empty($value)) {
      if (strpos($value, $suffix) === 0) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  function unique($value, $name, $key, $table, $excluding_id = null) {
    $result = true;
    $message = $this->translation->translate($name) . " " . $this->translation->translate("can not be duplicated");
    if (!$this->is_empty($value)) {
      $extensions = array(
        "conjunction" => "",
        "key" => "`" . $key . "`",
        "operator" => "LIKE",
        "value" => "'" . $value . "'",
      );
      if ($excluding_id) {
        array_push(
          $extensions,
          array(
            "conjunction" => "AND",
            "key" => "`id`",
            "operator" => "!=",
            "value" => "'" . $excluding_id . "'"
          )
        );
      }
      if (
        $this->database->select(
          $table, 
          array($key), 
          null, 
          $extensions, 
          true
        )
      ) {
        $result = false;
        throw new Exception($message, 400);
      }
    }
    return $result;
  }

  private function is_empty($value) {
    $result = false;
    if (!isset($value) || (empty($value) && $value !== 0)) {
      $result = true;
    }
    return $result;
  }

}